        $format = '%s, %s, %s, and %s. %s since %s.';

        return sprintf( $format,
                        $this->real_jobs[array_rand($this->real_jobs)],
                        $this->aspirational_jobs[array_rand($this->aspirational_jobs)],
                        $this->family_titles[array_rand($this->family_titles)],
